import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Notes({ token }) {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [editingNote, setEditingNote] = useState(null);
  const [editContent, setEditContent] = useState('');

  // Notları Fetch Etme
  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/notes', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setNotes(response.data);
      } catch (error) {
        console.error('Notları getirme başarısız', error);
      }
    };

    fetchNotes();
  }, [token]);

  // Not Ekleme
  const addNote = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/notes', 
        { content: newNote },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNotes([...notes, response.data]);
      setNewNote('');
    } catch (error) {
      console.error('Not ekleme başarısız', error);
    }
  };

  // Not Silme
  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotes(notes.filter(note => note.id !== id));
    } catch (error) {
      console.error('Not silme başarısız', error);
    }
  };

  // Not Güncelleme
  const updateNote = async () => {
    try {
      const response = await axios.put(`http://localhost:5000/api/notes/${editingNote.id}`, 
        { content: editContent },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNotes(notes.map(note => note.id === editingNote.id ? response.data : note));
      setEditingNote(null);
      setEditContent('');
    } catch (error) {
      console.error('Not güncelleme başarısız', error);
    }
  };

  return (
    <div>
      <h2>Notlarınız</h2>
      <ul>
        {notes.map((note) => (
          <li key={note.id}>
            {note.content}
            <button onClick={() => deleteNote(note.id)}>Sil</button>
            <button onClick={() => { setEditingNote(note); setEditContent(note.content); }}>Düzenle</button>
          </li>
        ))}
      </ul>
      {editingNote ? (
        <div>
          <h3>Notu Düzenle</h3>
          <input
            type="text"
            value={editContent}
            onChange={(e) => setEditContent(e.target.value)}
          />
          <button onClick={updateNote}>Güncelle</button>
          <button onClick={() => setEditingNote(null)}>İptal</button>
        </div>
      ) : (
        <div>
          <h3>Yeni Not Ekle</h3>
          <input
            type="text"
            value={newNote}
            onChange={(e) => setNewNote(e.target.value)}
          />
          <button onClick={addNote}>Ekle</button>
        </div>
      )}
    </div>
  );
}

export default Notes;
